import React, { useState } from 'react';
export default function PizzaForm() {
    const [size, setSize] = useState('medium');

    const handleSizeChange = (event) => {
        setSize(event.target.value);
      };
      return(
        <form>
            <legend>Boyut Se√ß</legend>
            <label>
                Small
                <input  
                type="checkbox"
                value="small"
                checked={size === 'small'}
                onChange={handleSizeChange}
                >
                </input>
            </label>
            <br />
            <label>
                Medium
                <input 
                 type="checkbox"
                 value="medium"
                 checked={size === 'medium'}
                 onChange={handleSizeChange}
                 >
                </input>
            </label>
            <br />
            <label>
                Large 
                <input 
                 type="checkbox"
                 value="large"
                 checked={size === 'large'}
                 onChange={handleSizeChange}
                 >
                </input>
            </label>
            <br/>
        </form>
      )
}
