import React from "react";
export default function Note(){
    return (
   <div className="Note">
    <h2>Sipariş Notu</h2>
    <from>
    <textarea name="not" placeholder="Siparişine eklemek istediğin bir not var mı?"></textarea>
    </from>
    <label>Pizza Sayısı:</label>
<input type="number" name="pizzasayisi" min="1" max="10" value="1" />
<button onClick={() => setPizzaCount(pizzaCount + 1)}>+</button>
<button onClick={() => setPizzaCount(pizzaCount - 1)}>-</button> 
   </div>
   );
};
