import React, { useState } from 'react';
export default function PizzaForm() {
    const [size, setSize] = useState('medium');

    const handleSizeChange = (event) => {
        setSize(event.target.value);
      };
      return(
        <form>
            <legend>Boyut Seç</legend>
            <label>
                Small
                <input  
                type="checkbox"
                value="small"
                checked={size === 'small'}
                onChange={handleSizeChange}
                >
                </input>
            </label>
            <br />
            <label>
                Medium
                <input 
                 type="checkbox"
                 value="medium"
                 checked={size === 'medium'}
                 onChange={handleSizeChange}
                 >
                </input>
                
            </label>
            <br />
            <label>
                Large 
                <input 
                 type="checkbox"
                 value="large"
                 checked={size === 'large'}
                 onChange={handleSizeChange}
                 >
                </input>
            </label>
            <br/>
        </form>
      );
};

 function PizzaOrderForm () {
    const [toppings,setTopings]=useState([
        { id: "1", value: "Papperroni", status: true },
        { id: "2", value: "Domates", status: false },
        { id: "3", value: "Biber", status: false },
        { id: "4", value: "Sosis", status: true },
        { id: "5", value: "Mısır", status: true },
        { id: "6", value: "Sucuk", status: false },
        { id: "7", value: "Kanada Jambonu", status: false },
        { id: "8", value: "Pastırma", status: false },
        { id: "9", value: "Ananas", status: true },
        { id: "10", value: "Tavuk Izgara", status: false },
        { id: "11", value: "Jalepeno", status: true },
        { id: "12", value: "Kabak", status: true },
        { id: "13", value: "Soğan", status: false },
        { id: "14", value: "Sarımsak", status: false }
    

    ]);

    const handleToppingsChange = (event) => {
        const selectedToppings = toppings.filter((topping) => event.target.value.includes(topping.name));
        console.log(selectedToppings);
      };

      return (
        <div>
          <h2>Malzeme Seçimi</h2>
          {toppings.map((topping) => (
            <label key={topping.id}>
              <input type="checkbox" value={topping.name} onChange={handleToppingsChange} />
              {topping.name}
            </label>
          ))}
        </div>
      );
    
}
